import styled from "styled-components";

import ButtonProps from "./button.model";

export const ButtonStyled = styled.button`
  display: flex;
  align-items: center;
  justify-content: center;
  border: none;
  width: 100%;
  height: 50px;
  border-radius: 14px;
  cursor: pointer;
  background: ${(props: ButtonProps) => props.appeareance};
  label
  * {
    color: #fff;
  }
`;
